				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$2mKOH+2nHOH\to 2m{K}^{+}+2(n+m)O{H}^{-}+2n{H}^{+}\,({\rm{hydrolysis}})$$\end{document}4nTi+nH++(n+m)OH−→[TinO(n+m)](m)−+(2n+m2)H2(polytitanateanionformation)\documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym}
