				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}
$$ 2 {\text{H}}^{ + } + {\text{ 2e}}^{ - } + {\text{ 4ATP }} \to {\text{ H}}_{ 2} + {\text{ 4ADP }} + {\text{ 4P}}_{\text{i}} $$
\end{document}

