				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$N_{{{\text{vc}}}} = \frac{{v_{x} \mu_{{{\text{nw}}}} }}{{k_{{{\text{av}}}} }}\frac{{\Delta z^{2} }}{\Delta x}\frac{1}{{\frac{{{\text{dP}}_{c} }}{{{\text{dS}}_{w} }}}}$$\end{document}

and8Ngv=ΔρgkavvxμnwΔxΔz\documentclass[12pt]{minimal}
