				\begin{document}$$ {\text{NDVI}}_{\text{OLI}} = \frac{{\rho {\text{band}}5_{OLI} - \rho {\text{band}}4_{\text{OLI}} }}{{\rho {\text{band}}5_{OLI} + \rho {\text{band}}4_{\text{OLI}} }} . $$\end{document}

