				\usepackage{amsmath}
				\usepackage{wasysym}
				\usepackage{amsfonts}
				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\hbox {Mg}_x\hbox {Fe}_{(1-x)}\hbox {SiO}_3$$\end{document}, respectively, with 0<x<1\documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym}
