				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$${P}_{{{{{{\rm{g}}}}}}}=\frac{\left(2\times 48.5\times {10}^{15}\times 20000\right)}{\left(365\times 86400\times3.625\times {10}^{14}\right)}=0.169\ {{{{{\rm{W}}}}}}\cdot {{{{{{\rm{m}}}}}}}^{-2}$$\end{document}
