				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\begin{aligned} E_x[\log (D(x))]+E_z[\log (1-D(G(z))]. \end{aligned}$$\end{document}

