				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\eta _{{\mathrm{mp}}} = 1 - \sqrt {T_{\mathrm{c}}/T_{\mathrm{h}}}\, < \, \eta _{\mathrm{c}}$$\end{document}.

