				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\frac{\partial A(r,z,T)}{\partial z}=(\hat{L}+\hat{N})A(r,z,T\mathrm{).}$$\end{document}

