				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$k_{ic}$$\end{document} and ISC kISC\documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym}
				\usepackage{amsfonts}
				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$k_{ISC}$$\end{document} against the rate of fluorescence kr\documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym}
				\usepackage{amsfonts}
				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$k_r$$\end{document}:3PLQY=krkr+kic+kISC\documentclass[12pt]{minimal}
				\usepackage{amsmath}
				\usepackage{wasysym}
