				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$${\text{ln}}\frac{{P_{nano}^{eq} }}{{P_{bulk}^{eq} }} = \frac{1}{RT}\left( {\frac{{3V_{{{\text{Mg}}}} \gamma_{{{\text{Mg}}}} }}{{r_{{{\text{Mg}}}} }} - \frac{{3V_{{{\text{MgH}}_{2} }} \gamma_{{{\text{MgH}}_{2} }} }}{{r_{{{\text{MgH}}_{2} }} }}} \right)$$\end{document}

