				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$$\tau (\lambda )=\exp (\,-\,{\beta }_{{\rm{sur}}}^{\ast }\cdot {\lambda }^{-{a}^{\ast }}\,)+{\gamma }^{\ast }$$\end{document}

