				\usepackage{amssymb}
				\usepackage{amsbsy}
				\usepackage{mathrsfs}
				\usepackage{upgreek}
				\setlength{\oddsidemargin}{-69pt}
				\begin{document}$${k}_{c,j}=\left\{\begin{array}{ll}{k}_{c,{{{{{\rm{in}}}}}}}&{{j}}\in {{{{{{{\rm{I}}}}}}}}\,\,{{{{{{{\rm{stage}}}}}}}}\\ j\cdot \frac{{k}_{c,{{{{{\rm{mid}}}}}}}-{k}_{c,{{{{{\rm{in}}}}}}}}{j-{l}_{{{{{{\rm{I}}}}}}}}&{{j}}\in {{{{{{{\rm{II}}}}}}}}\,\,{{{{{{{\rm{stage}}}}}}}}\\ {k}_{c,{{{{{\rm{mid}}}}}}}&{{j}}\in {{{{{{{\rm{III}}}}}}}}\,\,{{{{{{{\rm{stage}}}}}}}}\\ j\cdot \frac{{k}_{c,{{{{{\rm{f}}}}}}}-{k}_{c,{{{{{\rm{mid}}}}}}}}{j-{l}_{{{{{{\rm{I}}}}}}}-{l}_{{{{{{\rm{II}}}}}}}-{l}_{{{{{{\rm{III}}}}}}}}&{{j}}\in {{{{{{{\rm{IV}}}}}}}}\,\,{{{{{{{\rm{stage}}}}}}}}\end{array}\right.$$\end{document}

